               WRITE(IREPT,1)'NORMALIZATION'                            02 00250
               WRITE(ITRMOU,1)'NORMALIZATION'                           02 00260
            ELSE                                                        02 00270
               WRITE(IREPT,2)'NORMALIZATION'                            02 00280
