      REAL RTYPE(MAXDCY),ENDQ(MAXDCY),ENDDQ(MAXDCY),ENDBR(MAXDCY),      07 00900
     2  ENDDBR(MAXDCY),ENDN(MAXDCY,10),ENDDN(MAXDCY,10),ELIS(MAXDCY),   07 00910
