      INTEGER NSTYP0                                                    07 00960
      REAL ES0(GMA),DES0(GMA),RS0(GMA),DRS0(GMA),RICC(GMA),DRICC(GMA),  07 00970
     2  RICK(GMA),DRICK(GMA),RICL(GMA),DRICL(GMA),RIS(GMA),DRIS(GMA),   07 00980
