      COMMON /STYP0/ES0,DES0,RS0,DRS0,RICC,DRICC,RICK,DRICK,RICL,DRICL, 07 01000
     2  RIS,DRIS,RTYP0,AVE0,DAVE0,NSTYP0                                07 01010
