      INTEGER NSTYP1                                                    07 01030
      REAL ES1(AM),DES1(AM),RS1(AM),DRS1(AM),RTYP1(AM),TYP1(AM),AVE1,   07 01040
     2  DAVE1                                                           07 01050
      COMMON /STYP1/ES1,DES1,RS1,DRS1,RTYP1,TYP1,AVE1,DAVE1,NSTYP1      07 01060
