      INTEGER NSTYP2                                                    07 01080
      REAL ES2(AM),DES2(AM),RS2(AM),DRS2(AM),RIS2(AM),DRIS2(AM),        07 01090
     2  RTYP2(AM),TYP2(AM),AVE2,DAVE2                                   07 01100
      COMMON /STYP2/ES2,DES2,RS2,DRS2,RIS2,DRIS2,RTYP2,TYP2,AVE2,DAVE2, 07 01110
     2  NSTYP2                                                          07 01120
