      INTEGER IREPT,ITRMOU,IATOMB,IMASSB                                07 01140
      COMMON /RPTUNT/IREPT                                              07 01150
      COMMON /TTYUNT/ITRMOU                                             07 01160
      COMMON /BINDAT/IATOMB,IMASSB                                      07 01170
