      INTEGER NGRIDI                                                    07 01190
      REAL EBIS(NGRMAX),RIBIS(NGRMAX),DRIBIS(NGRMAX),EBARIS(NGRMAX),    07 01200
     2  DEBRIS(NGRMAX),SUMEB,DSUMEB                                     07 01210
      COMMON /IBSPCT/NGRIDI,EBIS,RIBIS,DRIBIS,EBARIS,DEBRIS,SUMEB,DSUMEB07 01220
