      INTEGER I,I1,II,IK,IP1,IS,IZA,J,J1,J2,JJ,K,L,LX,NH1,NH2,NH3,NH4,  07 01240
     2  NHIGH,NLOW                                                      07 01250
      REAL A,AMASS,B,C,D,D1,D2,D3,D4,D5,DRI1,DUM,DX,F,RI1,RI2,X         07 01260
