C                                                                       09 00480
      INTEGER I,I1,II,J,JJ,NHIGH,NLOW                                   09 00490
C                                                                       09 00500
C         Sort together Gammas and X rays                               09 00510
C                                                                       09 00520
      IF (NG+NE .GT. 0) THEN                                            09 00530
