150      CONTINUE                                                       09 00750
      ENDIF                                                             09 00760
C                                                                       09 00770
C  B- and B+ already sorted by energy                                   09 00780
C                                                                       09 00790
C                                                                       09 00800
      DO 200 I=1,5                                                      09 00810
         IF (OMIT(I) .NE. 0.0) EOMIT(I)=EOMIT(I) / OMIT(I)              09 00820
200   CONTINUE                                                          09 00830
C                                                                       09 00840
      RETURN                                                            09 00850
      END                                                               09 00860
