               CALL MESSAG(NMESS,5,'DI=0 SET')                          22 01230
               DBMI(NB)=0.                                              22 01240
            ELSE                                                        22 01250
               CALL LOWLMT(BMI(NB),DBMI(NB),NMESS)                      22 01260
