        REAL FNEW                                                       71 00140
        EXTERNAL FNEW                                                   71 00150
C                                                                       71 00160
        REAL Y                                                          71 00170
C                                                                       71 00180
        Y=FNEW(X)                                                       71 00190
        IF(X .NE. 0. .AND. Y .NE. 0.)THEN                               71 00200
           XF=X*Y                                                       71 00210
