        B1S2=1.-(4.*ETA1*B1S2/(3.*(1.+ETA1)))                           82 00640
        B1S2=B1S2*B1S2                                                  82 00650
C                                                                       82 00660
        RETURN                                                          82 00670
        END                                                             82 00680
