           CALL POLDRI(K,NTAB,KTAB,Q,Y,DY)                              00 00510
           IF(ABS(DY) .LE. PREC*ABS(Y))THEN                             00 00520
              Q2P2=Y*Y                                                  00 00530
              RETURN                                                    00 00540
