      INTEGER IDECAY(GMA)                                               06 00470
      REAL EG(GMA),DEG(GMA),IG(GMA),DIG(GMA),ICE(GMA,5),DICE(GMA,5),E2I,06 00480
     2  DE2I                                                            06 00490
